#
# Procedure "p_insert"
#

DROP PROCEDURE IF EXISTS `p_insert`;
CREATE PROCEDURE `p_insert`(v1 double, v2 double)
BEGIN
     declare val double;
          /*
          กดก่อน = v1 ต้นทาง  
          กดหลัง = v2 ปลายทาง
          เงื่อนไขในการเลือกปลายทาง กรณีไม่เจอจะแทนเป็น 0
          */
                      select ifnull(
                          (select seq
                          from superaward_status
                          where seq < v2
                          order by seq desc
                          limit 1)
                        ,0
                        ) into val;
         
       update superaward_status
       set seq = (val+v2)/2
       where seq = v1;
END;

