DROP PROCEDURE IF EXISTS `p_swap`;
CREATE PROCEDURE `p_swap`(v1 double, v2 double)
BEGIN
     
     declare swap1 double;
     declare swap2 double;
     
     select number
     into  swap1
     from superaward_status
     where seq = v1;
     
     select number
     into  swap2
     from superaward_status
     where seq = v2;
      
   
     update superaward_status
      set 
        seq = v1
     where number = swap2;
     
     update superaward_status
      set 
        seq = v2
     where number = swap1;
      
END;

